import React, { useEffect, useState } from "react";
import axios from "axios";

export default function KPICards() {
  const [kpi, setKpi] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:4004/rest/purchase-order/getKPIs")
      .then((res) => {
        setKpi(res.data);
      });
  }, []);

  if (!kpi) return <div>Loading...</div>;

  return (
    <div className="grid grid-cols-2 md:grid-cols-3 gap-6 p-6">
      <Card title="Total Orders" value={kpi.totalOrders} />
      <Card title="Pending" value={kpi.pendingOrders} />
      <Card title="Approved" value={kpi.approvedOrders} />
      <Card title="Ordered" value={kpi.orderedOrders} />
      <Card
        title="Total Amount"
        value={`₹${kpi.totalAmount.toLocaleString()}`}
      />
    </div>
  );
}

function Card({ title, value }) {
  return (
    <div className="bg-white shadow-md rounded-xl p-4 text-center">
      <h2 className="text-sm text-gray-500">{title}</h2>
      <p className="text-2xl font-semibold text-gray-800">{value}</p>
    </div>
  );
}
