import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Box,
  Grid,
  Text,
  Stat,
  StatLabel,
  StatNumber,
  useColorModeValue,
  Spinner,
} from "@chakra-ui/react";

export default function KPICards() {
  const [kpi, setKpi] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:4004/rest/purchase-order/getKPIs")
      .then((res) => setKpi(res.data));
  }, []);

  if (!kpi) {
    return (
      <Box p={8}>
        <Spinner size="lg" />
      </Box>
    );
  }

  return (
    <Box p={6}>
      <Grid templateColumns={["repeat(2, 1fr)", "repeat(3, 1fr)"]} gap={6}>
        <KPICard title="Total Orders" value={kpi.totalOrders} />
        <KPICard title="Pending" value={kpi.pendingOrders} />
        <KPICard title="Approved" value={kpi.approvedOrders} />
        <KPICard title="Ordered" value={kpi.orderedOrders} />
        <KPICard
          title="Total Amount"
          value={`₹${kpi.totalAmount.toLocaleString()}`}
        />
      </Grid>
    </Box>
  );
}

function KPICard({ title, value }) {
  const cardBg = useColorModeValue("white", "gray.700");

  return (
    <Box bg={cardBg} p={6} borderRadius="xl" boxShadow="md" textAlign="center">
      <Stat>
        <StatLabel color="gray.500">{title}</StatLabel>
        <StatNumber fontSize="2xl" color="gray.800">
          {value}
        </StatNumber>
      </Stat>
    </Box>
  );
}
